package endpoint

import (
	"github.com/kassisol/hbm/docker/allow"
	"github.com/kassisol/hbm/docker/dcb"
	"github.com/kassisol/hbm/pkg/uri"
)

func GetUris() *uri.URIs {
	uris := uri.New()

	uris.Register("GET", `^/containers/(.+)/archive`, allow.True, dcb.ContainerArchive, "container_archive", "archive", "Get an archive of a filesystem resource in a container")
	uris.Register("GET", `^/containers/(.+)/attach/ws`, allow.True, dcb.ContainerAttachWS, "container_attach_ws", "attach_ws", "Attach to a running container (websocket)")
	uris.Register("GET", `^/containers/(.+)/changes`, allow.True, dcb.ContainerChanges, "container_changes", "events", "Get real time events from the server")
	uris.Register("GET", `^/containers/(.+)/export`, allow.True, dcb.ContainerExport, "container_export", "container export", "Export a container's filesystem as a tar archive")
	uris.Register("GET", `^/containers/(.+)/json`, allow.True, dcb.ContainerInspect, "container_inspect", "container inspect", "Display detailed information on one or more containers")
	uris.Register("GET", `^/containers/(.+)/logs`, allow.True, dcb.ContainerLogs, "container_logs", "container logs", "Fetch the logs of a container")
	uris.Register("GET", `^/containers/(.+)/stats`, allow.True, dcb.ContainerStats, "container_stats", "container stats", "Display a live stream of container(s) resource usage statistics")
	uris.Register("GET", `^/containers/(.+)/top`, allow.True, dcb.ContainerTop, "container_top", "container top", "Display the running processes of a container")
	uris.Register("GET", `^/containers/json`, allow.True, dcb.ContainerList, "container_list", "container ls", "List containers")
	uris.Register("POST", `^/containers/(.+)/attach`, allow.True, dcb.ContainerAttach, "container_attach", "container attach", "Attach to a running container")
	uris.Register("POST", `^/containers/(.+)/kill`, allow.True, dcb.ContainerKill, "container_kill", "container kill", "Kill one or more running containers")
	uris.Register("POST", `^/containers/(.+)/pause`, allow.True, dcb.ContainerPause, "container_pause", "container pause", "Pause all processes within one or more containers")
	uris.Register("POST", `^/containers/(.+)/rename`, allow.True, dcb.ContainerRename, "container_rename", "container rename", "Rename a container")
	uris.Register("POST", `^/containers/(.+)/resize`, allow.True, dcb.ContainerResize, "container_resize", "resize", "Resize a container TTY")
	uris.Register("POST", `^/containers/(.+)/restart`, allow.True, dcb.ContainerRestart, "container_restart", "container restart", "Restart one or more containers")
	uris.Register("POST", `^/containers/(.+)/start`, allow.True, dcb.ContainerStart, "container_start", "container start", "Start one or more stopped containers")
	uris.Register("POST", `^/containers/(.+)/stop`, allow.True, dcb.ContainerStop, "container_stop", "container stop", "Stop one or more running container")
	uris.Register("POST", `^/containers/(.+)/unpause`, allow.True, dcb.ContainerUnpause, "container_unpause", "container unpause", "Unpause all processes within one or more containers")
	uris.Register("POST", `^/containers/(.+)/update`, allow.True, dcb.ContainerUpdate, "container_update", "container update", "Update configuration of one or more containers")
	uris.Register("POST", `^/containers/(.+)/wait`, allow.True, dcb.ContainerWait, "container_wait", "container wait", "Block until one or more containers stop, then print their exit codes")
	uris.Register("POST", `^/containers/create`, allow.ContainerCreate, dcb.ContainerCreate, "container_create", "container create", "Create a new container")
	uris.Register("POST", `^/containers/prune`, allow.True, dcb.ContainerPrune, "container_prune", "container prune", "Remove all stopped containers")
	uris.Register("PUT", `^/containers/(.+)/archive`, allow.True, dcb.ContainerArchiveExtract, "container_archive_extract", "archive", "Extract an archive of files or folders to a directory in a container")
	uris.Register("DELETE", `^/containers/(.+)`, allow.True, dcb.ContainerRemove, "container_remove", "container rm", "Remove one or more containers")
	uris.Register("HEAD", `^/containers/(.+)/archive`, allow.True, dcb.ContainerArchiveInfo, "container_archive_info", "archive", "Retrieving information about files and folders in a container")

	uris.Register("GET", `^/images/(.+)/get`, allow.True, dcb.ImageSaveImage, "image_save_image", "image save", "Save one or more images to a tar archive")
	uris.Register("GET", `^/images/(.+)/history`, allow.True, dcb.ImageHistory, "image_history", "image history", "Show the history of an image")
	uris.Register("GET", `^/images/(.+)/json`, allow.True, dcb.ImageInspect, "image_inspect", "image inspect", "Display detailed information on one or more images")
	uris.Register("GET", `^/images/get`, allow.True, dcb.ImageSaveImages, "image_save_images", "image save", "Save one or more images to a tar archive")
	uris.Register("GET", `^/images/json`, allow.True, dcb.ImageList, "image_list", "image ls", "List images")
	uris.Register("GET", `^/images/search`, allow.True, dcb.ImageSearch, "image_search", "search", "Search the Docker Hub for images")
	uris.Register("POST", `^/build`, allow.True, dcb.ImageBuild, "image_build", "image build", "Build an image from a Dockerfile")
	uris.Register("POST", `^/commit`, allow.True, dcb.Commit, "commit", "commit", "Create a new image from a container's changes")
	uris.Register("POST", `^/images/(.+)/push`, allow.True, dcb.ImagePush, "image_push", "image push", "Push an image or a repository to a registry")
	uris.Register("POST", `^/images/(.+)/tag`, allow.True, dcb.ImageTag, "image_tag", "image tag", "Create a tag TARGET_IMAGE that refers to SOURCE_IMAGE")
	uris.Register("POST", `^/images/create`, allow.ImageCreate, dcb.ImageCreate, "image_create", "image pull", "Pull an image or a repository from a registry")
	uris.Register("POST", `^/images/load`, allow.True, dcb.ImageLoad, "image_load", "image load", "Load an image from a tar archive or STDIN")
	uris.Register("POST", `^/images/prune`, allow.True, dcb.ImagePrune, "image_prune", "image prune", "Delete unused images")
	uris.Register("DELETE", `^/images/(.+)`, allow.True, dcb.ImageRemove, "image_remove", "image rm", "Remove one or more images")

	uris.Register("GET", `^/networks$`, allow.True, dcb.NetworkList, "network_list", "network ls", "List all networks")
	uris.Register("GET", `^/networks/(.+)`, allow.True, dcb.NetworkInspect, "network_inspect", "network inspect", "Display detailed network information")
	uris.Register("POST", `^/networks/(.+)/connect`, allow.True, dcb.NetworkConnect, "network_connect", "network connect", "Connect container to a network")
	uris.Register("POST", `^/networks/(.+)/disconnect`, allow.True, dcb.NetworkDisconnect, "network_disconnect", "network disconnect", "Disconnect container from a network")
	uris.Register("POST", `^/networks/create`, allow.True, dcb.NetworkCreate, "network_create", "network create", "Create a network")
	uris.Register("POST", `^/networks/prune`, allow.True, dcb.NetworkPrune, "network_prune", "network prune", "Delete unused networks")
	uris.Register("DELETE", `^/networks/(.+)`, allow.True, dcb.NetworkRemove, "network_remove", "network rm", "Remove a network")

	uris.Register("GET", `^/volumes$`, allow.True, dcb.VolumeList, "volume_list", "volume ls", "List volumes")
	uris.Register("GET", `^/volumes/(.+)`, allow.True, dcb.VolumeInspect, "volume_inspect", "volume inspect", "Return low-level information on a volume")
	uris.Register("POST", `^/volumes/create`, allow.True, dcb.VolumeCreate, "volume_create", "volume create", "Create a volume")
	uris.Register("POST", `^/volumes/prune`, allow.True, dcb.VolumePrune, "volume_prune", "volume prune", "Delete unused volumes")
	uris.Register("DELETE", `^/volumes/(.+)`, allow.True, dcb.VolumeRemove, "volume_remove", "volume rm", "Remove a volume")

	uris.Register("GET", `^/exec/(.+)/json`, allow.True, dcb.ExecInspect, "exec_inspect", "exec", "Exec Inspect")
	uris.Register("POST", `^/containers/(.+)/exec`, allow.True, dcb.ContainerExecCreate, "container_exec_create", "exec", "Run a command in a running container")
	uris.Register("POST", `^/exec/(.+)/resize`, allow.True, dcb.ExecResize, "exec_resize", "exec", "Exec Resize")
	uris.Register("POST", `^/exec/(.+)/start`, allow.True, dcb.ExecStart, "exec_start", "exec", "Exec Start")

	uris.Register("GET", `^/swarm/unlockkey`, allow.True, dcb.SwarmUnlockKey, "swarm_unlock_key", "swarm unlock", "Get the unlock key")
	uris.Register("GET", `^/swarm`, allow.True, dcb.SwarmInspect, "swarm_inspect", "swarm info", "Get swarm info")
	uris.Register("POST", `^/swarm/init`, allow.True, dcb.SwarmInit, "swarm_init", "swarm init", "Initialize a new swarm")
	uris.Register("POST", `^/swarm/join`, allow.True, dcb.SwarmJoin, "swarm_join", "swarm join", "Join an existing swarm")
	uris.Register("POST", `^/swarm/leave`, allow.True, dcb.SwarmLeave, "swarm_leave", "swarm leave", "Leave a swarm")
	uris.Register("POST", `^/swarm/unlock`, allow.True, dcb.SwarmUnlock, "swarm_unlock", "swarm unlock", "Unlock a locked manager")
	uris.Register("POST", `^/swarm/update`, allow.True, dcb.SwarmUpdate, "swarm_update", "swarm update", "Update a swarm")

	uris.Register("GET", `^/nodes/(.+)`, allow.True, dcb.NodeInspect, "node_inspect", "node inspect", "Return low-level information on the node id")
	uris.Register("GET", `^/nodes`, allow.True, dcb.NodeList, "node_list", "node ls", "List nodes")
	uris.Register("POST", `^/nodes/(.+)/update`, allow.True, dcb.NodeUpdate, "node_update", "node update", "Update the node id")
	uris.Register("DELETE", `^/nodes/(.+)`, allow.True, dcb.NodeRemove, "node_remove", "node rm", "Remove a node [id] from the swarm")

	uris.Register("GET", `^/services/(.+)/logs`, allow.True, dcb.ServiceLogs, "service_logs", "service logs", "Get service logs")
	uris.Register("GET", `^/services/(.+)`, allow.True, dcb.ServiceInspect, "service_inspect", "service inspect", "Inspect a service")
	uris.Register("GET", `^/services`, allow.True, dcb.ServiceList, "service_list", "service ls", "List services")
	uris.Register("POST", `^/services/(.+)/update`, allow.ServiceCreate, dcb.ServiceUpdate, "service_update", "service update", "Update a service")
	uris.Register("POST", `^/services/create`, allow.ServiceCreate, dcb.ServiceCreate, "service_create", "service create", "Create a service")
	uris.Register("DELETE", `^/services/(.+)`, allow.True, dcb.ServiceRemove, "service_remove", "service rm", "Remove a service")

	uris.Register("GET", `^/tasks/(.+)/logs`, allow.True, dcb.TaskLogs, "task_logs", "task logs", "Get task logs")
	uris.Register("GET", `^/tasks/(.+)`, allow.True, dcb.TaskInspect, "task_inspect", "stask tasks", "Get details on a task")
	uris.Register("GET", `^/tasks`, allow.True, dcb.TaskList, "task_list", "stask services", "List tasks")

	uris.Register("GET", `^/secrets/(.+)`, allow.True, dcb.SecretInspect, "secret_inspect", "secret inspect", "Inspect a secret")
	uris.Register("GET", `^/secrets`, allow.True, dcb.SecretList, "secret_list", "secret ls", "List secrets")
	uris.Register("POST", `^/secrets/(.+)/update`, allow.True, dcb.SecretUpdate, "secret_update", "secret update", "Update a secret")
	uris.Register("POST", `^/secrets/create`, allow.True, dcb.SecretCreate, "secret_create", "secret create", "Create a secret")
	uris.Register("DELETE", `^/secrets/(.+)`, allow.True, dcb.SecretRemove, "secret_remove", "secret rm", "Delete a secret")

	uris.Register("GET", `^/plugins/(.+)/json`, allow.True, dcb.PluginInspect, "plugin_inspect", "plugin inspect", "Inspect a plugin")
	uris.Register("GET", `^/plugins/privileges`, allow.True, dcb.PluginPrivileges, "plugin_privileges", "plugin ls", "Get plugin privileges")
	uris.Register("GET", `^/plugins`, allow.True, dcb.PluginList, "plugin_list", "plugin ls", "List plugins")
	uris.Register("POST", `^/plugins/(.+)/disable`, allow.True, dcb.PluginDisable, "plugin_disable", "plugin disable", "Disable a plugin")
	uris.Register("POST", `^/plugins/(.+)/enable`, allow.True, dcb.PluginEnable, "plugin_enable", "plugin enable", "Enable a plugin")
	uris.Register("POST", `^/plugins/(.+)/push`, allow.True, dcb.PluginPush, "plugin_push", "plugin push", "Push a plugin")
	uris.Register("POST", `^/plugins/(.+)/set`, allow.True, dcb.PluginSet, "plugin_set", "plugin set", "Configure a plugin")
	uris.Register("POST", `^/plugins/(.+)/upgrade`, allow.True, dcb.PluginUpgrade, "plugin_upgrade", "plugin upgrade", "Upgrade a plugin")
	uris.Register("POST", `^/plugins/create`, allow.True, dcb.PluginCreate, "plugin_create", "plugin create", "Create a plugin")
	uris.Register("POST", `^/plugins/pull`, allow.True, dcb.PluginPull, "plugin_pull", "plugin pull", "Install a plugin")
	uris.Register("DELETE", `^/plugins/(.+)`, allow.True, dcb.PluginRemove, "plugin_remove", "plugin rm", "Remove a plugin")

	uris.Register("POST", `^/auth`, allow.True, dcb.Auth, "auth", "login", "Log in to a Docker registry")
	uris.Register("GET", `^/info`, allow.True, dcb.Info, "info", "info", "Display system-wide information")
	uris.Register("GET", `^/version`, allow.True, dcb.Version, "version", "version", "Show the Docker version information")
	uris.Register("GET", `^/events`, allow.True, dcb.Events, "events", "events", "Monitor Docker's events")
	uris.Register("GET", `^/system/df`, allow.True, dcb.SystemDF, "system_df", "system df", "Get data usage information")

	uris.Register("GET", `^/configs/(.+)`, allow.True, dcb.ConfigInspect, "config_inspect", "config inspect", "Inspect a config")
	uris.Register("GET", `^/configs`, allow.True, dcb.ConfigList, "config_list", "config ls", "List configs")
	uris.Register("POST", `^/configs/(.+)/update`, allow.True, dcb.ConfigUpdate, "config_update", "config update", "Update a config")
	uris.Register("POST", `^/configs/create`, allow.True, dcb.ConfigCreate, "config_create", "config create", "Create a config")
	uris.Register("DELETE", `^/configs/(.+)`, allow.True, dcb.ConfigRemove, "config_remove", "config rm", "Delete a config")

	return uris
}
